import telebotfrom config import keys, TOKENfrom extensions import ConvertionException, Coverterbot = telebot.TeleBot(TOKEN)@bot.message_handler(commands=['start', 'help'])def handle_start_help(message):    text = 'Приветствую в моем боте, он умеет конвертировать валюты, для начала работы введите команду такого вида :\n<имя валюты> \<в какую коверитировать> \ <количество валюты> ' \'Узнать список валют - напиши команду /values'    bot.reply_to(message, text)@bot.message_handler(commands=['values'])def values (message: telebot.types.Message):    text = 'Доступные валюты для конвертации'    for key in keys.keys():        text = '\n'.join((text, key, ))    bot.reply_to(message, text)@bot.message_handler(content_types=['text', ])def convert(message: telebot.types.Message):    try:        values = message.text.split(' ')        if len(values) != 3:            raise ConvertionException('Введено более 3х переменных.')        quote, base, amount = values        total_base = Coverter.convert(quote, base, amount)    except ConvertionException as e:        bot.reply_to(message, f'Ошибка пользователя.\n{e}')    except Exception as e:        bot.reply_to(message, f'Не удалось обработать команду\n{e}')    else:        text = f'Цена {amount} {quote} в {base} - {total_base}'        bot.send_message(message.chat.id, text)bot.polling()